import React, { useState } from "react";
import axios from "axios";
import BottomLeft from "./BottomLeft";
import BottomRight from "./BottomRight";

export default function AppBottom(props) {
  let [ready, setReady] = useState(false);
  let [data, setData] = useState([]);
  let id = "9a3b65ea12488fdd227f03eda47a0bf6";
  let url = null;

  function dateFunc(currentDate) {
    let date = new Date(currentDate * 1000);
    let hours = date.getHours();
    let minutes = date.getMinutes();
    let day = date.getDay();
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    if (minutes < 10) {
      minutes = `0${minutes}`;
    }
    if (hours < 10) {
      hours = `0${hours}`;
    }
    return `Last Updated: ${days[day]} ${hours}:${minutes}`;
  }

  function updateWeather(response) {
    setData({
      city: response.data.name,
      temp: response.data.main.temp,
      hum: response.data.main.humidity,
      wind: response.data.main.wind.speed,
      imgSrc: `https://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`,
      time: dateFunc(response.data.dt),
      coord: response.data.coord,
    });
    setReady(true);
  }

  if (!ready) {
    url = `https://api.openweathermap.org/data/2.5/weather?q=${props.city}&appid=${id}&units=metric`;
    axios.get(url).then(updateWeather);
  } else {
    return (
      <div className="AppBottom">
        <BottomLeft city={data} />
        <BottomRight />
      </div>
    );
  }
  return <div>Loading...</div>;
}
