import React, { useState } from "react";
import axios from "axios";
//import WeatherIcon from "./elements/WeatherIcon";

export default function Forecast(props) {
  const [forecast, setForecast] = useState({});
  const [ready, setReady] = useState(false);
  function updateForecast(response) {
    setForecast(response.data.daily);
    console.log(forecast);
    setReady(true);
  }

  function formatDay(time) {
    let date = new Date(time * 1000);
    let day = date.getDay();
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    return days[day + 1];
  }

  if (!ready) {
    const apiKey = "6ca05cf5f1705e072a89eb633f8633e9";
    let url = `https://api.openweathermap.org/data/2.5/onecall?lat=${props.coord.lat}&lon=${props.coord.lon}&appid=${apiKey}&units=metric`;

    axios.get(url).then(updateForecast);

    return null;
  } else {
    return (
      <span className="BottomRight">
        <div className="card cardDay">
          <div className="card-body">
            <h3> {Math.round(forecast[0].temp.max)} °C</h3>
            <h3 className="min">{Math.round(forecast[0].temp.min)}°C</h3>
            <h4>{formatDay(props.date)}</h4>
          </div>
        </div>
      </span>
    );
  }
}
