import React, { useState } from "react";
import axios from "axios";
import AppBottom from "./bottom/AppBottom";

export default function Widget() {
  //let choice = ["Jakarta", "Beijing", "Amsterdam", "Hanoi"];
  let [city, setCity] = useState("");
  let [result, setResult] = useState("Jakarta");

  function dateFunc(currentDate) {
    let date = new Date(currentDate * 1000);
    let hours = date.getHours();
    let minutes = date.getMinutes();
    let day = date.getDay();
    let days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    if (minutes < 10) {
      minutes = `0${minutes}`;
    }
    if (hours < 10) {
      hours = `0${hours}`;
    }
    return `Last Updated: ${days[day]} ${hours}:${minutes}`;
  }
  function updateWeather(response) {
    setData({
      ready: true,
      city: response.data.name,
      temp: response.data.main.temp,
      hum: response.data.main.humidity,
      wind: response.data.main.wind.speed,
      imgSrc: `https://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`,
      time: dateFunc(response.data.dt),
      coord: response.data.coord,
    });
  }
  function updateCity(event) {
    setCity(event.target.value);
  }
  function submitCity(event) {
    event.preventDefault();
    setResult(city);
  }

  return (
    <div className="Widget">
      <div className="TopLeft">
        <div className="row">
          <div className="col-3">
            <span className="selected" id="jakarta">
              Jakarta
            </span>
          </div>
          <div className="col-3" id="beijing">
            Beijing
          </div>
          <div className="col-3" id="amsterdam">
            Amsterdam
          </div>
          <div className="col-3" id="hanoi">
            Hanoi
          </div>
        </div>
      </div>
      <div className="TopRight">
        <form id="city-form" onSubmit={submitCity}>
          <input
            type="text"
            id="city-input"
            placeholder="enter city"
            className="form"
            onChange={updateCity}
          />
          <input type="submit" value="search" className="searchButton" />
        </form>
        <button>current</button>
      </div>
      <AppBottom city={result} />
    </div>
  );
}
