import React, { useState } from "react";
import axios from "axios";

export default function BottomRight(props) {
  const [forecast, setForecast] = useState({});
  const [ready, setReady] = useState(false);
  function updateForecast(response) {
    setForecast(response.data.daily);
  }

  if (!ready) {
    const apiKey = "2d2ec576fd367f2433f859ba0c9d13e4";
    let url = `https://api.openweathermap.org/data/2.5/onecall?lat=${props.coord.lat}&lon=${props.coord.lon}&appid=${apiKey}`;

    axios.get(url).then(updateForecast);

    return null;
  } else {
    return (
      <span className="BottomRight">
        <div className="card cardDay">
          <div className="card-body">
            <i className="far fa-sun sunSmall"></i>
            <h3> °C</h3>
            <h3 className="min">28°C</h3>
            <h4>Monday</h4>
          </div>
        </div>
      </span>
    );
  }
}
